func:
// Set message topic
msg.topic = "mtec"

// Convert actual speed
if (msg.payload.mtec_pump_speed_actual < 20000)
{
    msg.payload.mtec_pump_speed_actual = 0.0;
}
else
{
    msg.payload.mtec_pump_speed_actual = ((msg.payload.mtec_pump_speed_actual - 26400) / (65535-26400)) * 65535
    msg.payload.mtec_pump_speed_actual = ((msg.payload.mtec_pump_speed_actual / 65535)  * (50 - 20) + 20) * (195.6 / 50)   
}

// Convert analog inputs to mA and V
msg.payload.mtec_io_ai1 = msg.payload.mtec_io_ai1 / 10000;
msg.payload.mtec_io_ai2 = msg.payload.mtec_io_ai2 / 10000;
msg.payload.mtec_io_ai3 = msg.payload.mtec_io_ai3 / 10000;
msg.payload.mtec_io_ai4 = (msg.payload.mtec_io_ai4 < 0) ? 0.0 : (msg.payload.mtec_io_ai4 / 65536 * 16.0 + 4.0);
msg.payload.mtec_io_ai5 = (msg.payload.mtec_io_ai5 < 0) ? 0.0 : (msg.payload.mtec_io_ai5 / 65536 * 16.0 + 4.0);


// Convert analog outputs to mA
msg.payload.mtec_io_ao1 = (msg.payload.mtec_io_ao1 < 0) ? 0.0 : (msg.payload.mtec_io_ao1 / 65536 * 16.0 + 4.0);
msg.payload.mtec_io_ao2 = (msg.payload.mtec_io_ao2 < 0) ? 0.0 : (msg.payload.mtec_io_ao2 / 65536 * 16.0 + 4.0);

// Convert other values
msg.payload.mtec_pump_speed_set = ((msg.payload.mtec_pump_speed_set / 65535)  * (50 - 20) + 20) * (195.6 / 50)
msg.payload.mtec_reverse_running_active = msg.payload.mtec_reverse_running_active == 0 ? true : false
msg.payload.mtec_water_control_active = msg.payload.mtec_water_control_active == false ? true : false
msg.payload.mtec_state_remote = msg.payload.mtec_state_remote == true ? false : true

return msg

if (msg.payload !== context.get('prev_mixer_run')) {

    context.set('prev_mixer_run', msg.payload);

	time = new Date().getTime() / 1000;
	queue = context.get('queue');
	prev_start = context.get('prev_mixer_start')
	prev_stop = context.get('prev_mixer_stop')
	prev_batch_time = context.get('batch_time');
	prev_interval_time = context.get('interval_time');

	if (msg.payload == false)
	{
	    batch_time = time - prev_start;
		context.set('prev_mixer_stop', time);
		context.set('batch_time', batch_time);

		msg.batch_time = batch_time;
		msg.interval_time = prev_interval_time;
		msg.prediction = prev_batch_time / prev_interval_time * 1.0;

		let sum = queue.reduce((acc, curr) => acc + curr, 0);
		msg.mean = sum / queue.length;

		return msg;
	}
	else
	{
		interval_time = time - prev_start;
	    msg.batch_time = prev_batch_time;
		msg.interval_time = interval_time;
		msg.prediction = prev_batch_time / interval_time * 1.0;

		queue.push(msg.prediction);

		if(queue.length > 8)
		{
		    queue.shift();
		}

		let sum = queue.reduce((acc, curr) => acc + curr, 0);
		msg.mean = sum / queue.length;

		context.set('queue', queue);
		context.set('interval_time', interval_time);
		context.set('prev_mixer_start', time);
		return msg;
	}
}

initialize:
// Code added here will be run once
// whenever the node is started.

context.set('prev_mixer_run', false);
context.set('prev_mixer_stop', new Date().getTime() / 1000);
context.set('prev_mixer_start', new Date().getTime() / 1000);
context.set('batch_time', 0.0);
context.set('interval_time', 0.0);
context.set('queue', []);
